import React, { useState } from "react";

const CodeEditor = ({ transcript }) => {
  const [generatedCode, setGeneratedCode] = useState("");
  const [loading, setLoading] = useState(false);

  const handleGenerateCode = async () => {
    if (!transcript) {
      alert("No transcript available to generate code.");
      return;
    }

    setLoading(true);
    try {
      const response = await fetch("http://localhost:5000/api/parse", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ transcript }),
      });

      const data = await response.json();
      setGeneratedCode(data.code || "// No code generated.");
    } catch (error) {
      console.error("Error generating code:", error);
      setGeneratedCode("// Error generating code.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4 w-full max-w-3xl mx-auto">
      <h2 className="text-xl font-semibold mb-2">ðŸŽ™ Transcript</h2>
      <div className="bg-gray-100 p-3 rounded mb-4 min-h-[80px]">
        {transcript || "Waiting for transcript..."}
      </div>

      <button
        onClick={handleGenerateCode}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:opacity-50"
        disabled={loading || !transcript}
      >
        {loading ? "Generating..." : "Generate Code"}
      </button>

      <h2 className="text-xl font-semibold mt-6 mb-2">ðŸ’» Generated Code</h2>
      <textarea
        className="w-full h-64 p-3 border rounded"
        value={generatedCode}
        readOnly
      />
    </div>
  );
};

export default CodeEditor;
