import React, { useEffect, useState } from 'react';
import axios from 'axios';

function CodeEditor() {
  const [code, setCode] = useState('');

  useEffect(() => {
    const storedCode = localStorage.getItem('generatedCode');
    if (storedCode) setCode(storedCode);
  }, []);

  const handleGenerateClick = async () => {
    try {
      const prompt = prompt("Enter your instruction to generate code:");
      if (!prompt) return;

      const response = await axios.post('http://localhost:5000/api/parse', {
        command: prompt
      });

      const newCode = response.data.code;
      setCode(newCode);
      localStorage.setItem('generatedCode', newCode);
    } catch (error) {
      alert("Code generation failed");
    }
  };

  return (
    <div className="p-4 bg-white rounded-lg shadow">
      <div className="flex justify-between items-center mb-2">
        <h2 className="text-xl font-semibold">ðŸ§  Generated Code</h2>
        <button onClick={handleGenerateClick} className="px-3 py-1 bg-blue-600 text-white rounded">Generate</button>
      </div>
      <textarea
        value={code}
        onChange={(e) => setCode(e.target.value)}
        rows={20}
        className="w-full p-2 border border-gray-300 rounded resize-none font-mono text-sm"
      />
    </div>
  );
}

export default CodeEditor;
