import React, { useState, useRef } from "react";

function VoiceControl({ onGenerate, setTranscript }) {
  const [isRecording, setIsRecording] = useState(false);
  const recognitionRef = useRef(null);

  const startListening = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Your browser does not support speech recognition.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = false;
    recognition.lang = "en-US";

    recognition.onresult = (event) => {
      const command = event.results[event.results.length - 1][0]
        .transcript.trim()
        .toLowerCase();
      console.log(`Heard: "${command}"`);

      setTranscript(command);

      // Voice commands
      if (command.includes("start recording")) {
        startListening(); // restart if not recording
      } else if (command.includes("stop recording")) {
        stopListening();
      } else if (command.includes("generate")) {
        onGenerate();
      }
    };

    recognition.start();
    recognitionRef.current = recognition;
    setIsRecording(true);
    console.log("ðŸŽ¤ Started recording...");
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      setIsRecording(false);
      console.log("ðŸ›‘ Stopped recording.");
    }
  };

  return (
    <button
      onClick={isRecording ? stopListening : startListening}
      className="bg-blue-500 text-white px-4 py-2 rounded"
    >
      {isRecording ? "Stop Recording" : "Start Recording"}
    </button>
  );
}

export default VoiceControl;
