import React, { useState, useRef, useEffect } from "react";
import axios from "axios";

function App() {
  const [transcript, setTranscript] = useState("");
  const [manualPrompt, setManualPrompt] = useState("");
  const [generatedCode, setGeneratedCode] = useState("");
  const [compileOutput, setCompileOutput] = useState("");
  const [loading, setLoading] = useState(false);

  const transcriptEndRef = useRef(null);
  const codeEndRef = useRef(null);

  const speak = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    window.speechSynthesis.speak(utterance);
  };

  const handleTranscribe = async (audioBlob) => {
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("file", audioBlob, "audio.wav");
      const res = await axios.post("http://localhost:5000/api/transcribe", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setTranscript(res.data.transcript || "");
    } catch (err) {
      console.error(err);
      setTranscript("‚ùå Error transcribing audio.");
      speak("Error transcribing audio.");
    }
    setLoading(false);
  };

  const handleGenerateCode = async () => {
    const promptToSend = transcript.trim() || manualPrompt.trim();
    if (!promptToSend) return;
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/generate-code", { prompt: promptToSend });
      setGeneratedCode(res.data.code || "// No code generated");
      speak("Code generated successfully.");
    } catch (err) {
      console.error(err);
      setGeneratedCode("// Error generating code");
      speak("Error generating code.");
    }
    setLoading(false);
  };

  const handleCompile = async () => {
    if (!generatedCode) return;
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/compile", { code: generatedCode, inputs: [] });
      const output = res.data.stdout || res.data.stderr || "// No output received";
      setCompileOutput(output);
      speak("Compilation finished.");
    } catch (err) {
      console.error(err);
      setCompileOutput("// Error compiling code");
      speak("Error compiling code.");
    }
    setLoading(false);
  };

  const handleSaveFile = () => {
    const blob = new Blob([generatedCode], { type: "text/plain" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "generated_code.py";
    a.click();
    window.URL.revokeObjectURL(url);
    speak("File saved successfully.");
  };

  useEffect(() => {
    transcriptEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [transcript]);

  useEffect(() => {
    codeEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [generatedCode, compileOutput]);

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-200 to-purple-50 flex flex-col items-center p-4">
      {/* Header */}
      <header className="w-full text-center py-6 bg-purple-600 text-white rounded-lg mb-6 shadow-lg">
        <h1 className="text-3xl font-bold">üé§ Code with Your Voice</h1>
        <p className="mt-1 text-sm opacity-90">No keyboard? No problem ‚Äî your voice is the new IDE.</p>
      </header>

      {/* Microphone */}
      <div className="mb-4">
        <button
          className="bg-green-500 hover:bg-green-600 p-6 rounded-full shadow-lg text-white text-2xl"
          onClick={() => speak("Microphone clicked!")} // Replace with handleTranscribe(audioBlob)
        >
          üé§
        </button>
        <p className="text-center mt-2 text-gray-700">Click to speak your code idea</p>
      </div>

      {/* Transcript */}
      <textarea
        className="w-full max-w-2xl p-3 mb-3 rounded-lg border shadow-sm focus:ring-2 focus:ring-purple-500 resize-none"
        rows="3"
        placeholder="Transcript will appear here..."
        value={transcript}
        onChange={(e) => setTranscript(e.target.value)}
      ></textarea>

      {/* Manual prompt */}
      <input
        className="w-full max-w-2xl p-3 mb-4 rounded-lg border shadow-sm focus:ring-2 focus:ring-purple-500"
        type="text"
        placeholder="Or type your prompt..."
        value={manualPrompt}
        onChange={(e) => setManualPrompt(e.target.value)}
      />

      {/* Generate Code button */}
      <button
        onClick={handleGenerateCode}
        disabled={loading}
        className="w-full max-w-2xl bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg shadow mb-4"
      >
        {loading ? "Generating..." : "‚ö° Generate Code"}
      </button>

      {/* Code output */}
      <div className="w-full max-w-2xl bg-gray-900 text-green-400 rounded-lg p-4 mb-4 overflow-auto min-h-[150px] font-mono">
        <pre>{generatedCode || "// Your generated code will appear here"}</pre>
        <div ref={codeEndRef}></div>
      </div>

      {/* Compile & Run button */}
      <button
        onClick={handleCompile}
        disabled={loading || !generatedCode}
        className="w-full max-w-2xl bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg shadow mb-4"
      >
        {loading ? "Running..." : "üõ†Ô∏è Compile & Run"}
      </button>

      {/* Program output */}
      <div className="w-full max-w-2xl bg-black text-green-400 rounded-lg p-4 mb-8 overflow-auto min-h-[100px] font-mono">
        <pre>{compileOutput || "// Output will appear here"}</pre>
        <div ref={transcriptEndRef}></div>
      </div>
    </div>
  );
}

export default App;
