import React, { useState, useRef } from "react";
import axios from "axios";

const VoiceControl = () => {
  const [prompt, setPrompt] = useState("");
  const [codeResult, setCodeResult] = useState("");
  const [isListening, setIsListening] = useState(false);
  const recognitionRef = useRef(null);

  // ðŸŽ¤ Start/Stop Voice Recognition
  const toggleListening = () => {
    if (!("webkitSpeechRecognition" in window)) {
      alert("Your browser does not support speech recognition");
      return;
    }

    if (!recognitionRef.current) {
      recognitionRef.current = new window.webkitSpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = false;
      recognitionRef.current.lang = "en-US";

      recognitionRef.current.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setPrompt(transcript);
        handleGenerateCode(transcript);
      };

      recognitionRef.current.onerror = (event) => {
        console.error("Speech recognition error:", event.error);
        setIsListening(false);
      };

      recognitionRef.current.onend = () => setIsListening(false);
    }

    if (isListening) {
      recognitionRef.current.stop();
    } else {
      recognitionRef.current.start();
    }

    setIsListening(!isListening);
  };

  // ðŸš€ Send prompt to backend
  const handleGenerateCode = async (customPrompt) => {
    try {
      const userPrompt = customPrompt || prompt;
      if (!userPrompt.trim()) {
        alert("Please provide a prompt!");
        return;
      }

      const res = await axios.post("http://localhost:5001/api/parse", {
        prompt: userPrompt,
      });

      setCodeResult(res.data.code || "No code generated");
    } catch (err) {
      console.error("Code generation failed:", err);
      alert(
        "Failed to generate code. Check if backend is running and CORS is enabled."
      );
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Voice + Manual Code Generator</h2>

      {/* Manual input */}
      <textarea
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        placeholder="Type your prompt or use voice..."
        rows={4}
        style={{ width: "100%", marginBottom: "10px" }}
      />

      {/* Buttons */}
      <div>
        <button onClick={toggleListening} style={{ marginRight: "10px" }}>
          {isListening ? "ðŸŽ™ Stop Listening" : "ðŸŽ¤ Start Listening"}
        </button>
        <button onClick={() => handleGenerateCode()}>Generate Code</button>
      </div>

      {/* Generated code output */}
      {codeResult && (
        <pre
          style={{
            background: "#eee",
            padding: "10px",
            marginTop: "20px",
            borderRadius: "5px",
          }}
        >
          {codeResult}
        </pre>
      )}
    </div>
  );
};

export default VoiceControl;
