import React, { useEffect, useState } from "react";

const CodeEditor = ({ transcript }) => {
  const [code, setCode] = useState("");

  useEffect(() => {
    if (transcript) {
      setCode(prev => prev + "\n" + transcript);
    }
  }, [transcript]);

  return (
    <div className="bg-gray-900 text-green-400 font-mono p-4 rounded h-[400px] overflow-y-scroll">
      <pre>{code}</pre>
    </div>
  );
};

export default CodeEditor;
