import React, { useState } from "react";
import MicInput from "./MicInput";
import CodeEditor from "./CodeEditor";
import VoiceControl from "./VoiceControl";

export default function App() {
  const [transcript, setTranscript] = useState("");
  const [generatedCode, setGeneratedCode] = useState("");
  const [output, setOutput] = useState("");
  const [loading, setLoading] = useState(false);

  const handleGenerateCode = async () => {
    if (!transcript.trim()) {
      alert("Please enter or speak your prompt first.");
      return;
    }
    setLoading(true);
    try {
      const res = await fetch("/api/generate-code", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt: transcript }),
      });
      if (!res.ok) throw new Error("Network response was not ok");
      const data = await res.json();
      setGeneratedCode(data.code || "");
      setOutput("");
    } catch (err) {
      console.error("Error generating code:", err);
      alert("Failed to generate code.");
    } finally {
      setLoading(false);
    }
  };

  const handleCompile = async () => {
    if (!generatedCode.trim()) {
      alert("No code to compile. Please generate or enter code first.");
      return;
    }
    setLoading(true);
    try {
      const res = await fetch("/api/compile", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code: generatedCode }),
      });
      if (!res.ok) throw new Error("Network response was not ok");
      const data = await res.json();
      setOutput(data.output || data.error || "");
    } catch (err) {
      console.error("Error compiling code:", err);
      alert("Failed to compile code.");
    } finally {
      setLoading(false);
    }
  };

  const handleSave = () => {
    if (!generatedCode.trim()) {
      alert("No code to save.");
      return;
    }
    const blob = new Blob([generatedCode], { type: "text/plain" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "code.py";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6 flex flex-col items-center">
      {/* Title */}
      <h1 className="text-4xl font-bold text-blue-600 mb-8 select-none">
        CodeVoice
      </h1>

      {/* Mic Input */}
      <div className="w-full max-w-3xl mb-6">
        <MicInput onTranscriptionComplete={setTranscript} />
      </div>

      {/* Editable Transcript Box */}
      <textarea
        value={transcript}
        onChange={(e) => setTranscript(e.target.value)}
        placeholder="Transcript will appear here... You can edit or type your prompt."
        className="w-full max-w-3xl h-28 p-3 border rounded mb-6 resize-y focus:outline-blue-500"
      />

      {/* Buttons */}
      <div className="flex flex-wrap gap-4 mb-8 justify-center max-w-3xl w-full">
        <button
          onClick={handleGenerateCode}
          disabled={loading}
          className={`px-5 py-3 rounded text-white font-semibold ${
            loading
              ? "bg-green-300 cursor-not-allowed"
              : "bg-green-600 hover:bg-green-700 cursor-pointer"
          }`}
          type="button"
        >
          {loading ? "Generating..." : "Generate Code"}
        </button>
        <button
          onClick={handleCompile}
          disabled={loading}
          className={`px-5 py-3 rounded text-white font-semibold ${
            loading
              ? "bg-yellow-300 cursor-not-allowed"
              : "bg-yellow-500 hover:bg-yellow-600 cursor-pointer"
          }`}
          type="button"
        >
          {loading ? "Compiling..." : "Compile"}
        </button>
        <button
          onClick={handleSave}
          disabled={loading}
          className={`px-5 py-3 rounded text-white font-semibold ${
            loading
              ? "bg-blue-300 cursor-not-allowed"
              : "bg-blue-600 hover:bg-blue-700 cursor-pointer"
          }`}
          type="button"
        >
          Save
        </button>
      </div>

      {/* Code Editor */}
      <div className="w-full max-w-3xl mb-6">
        <CodeEditor code={generatedCode} setCode={setGeneratedCode} />
      </div>

      {/* Output */}
      {output && (
        <div className="w-full max-w-3xl bg-white p-4 rounded shadow whitespace-pre-wrap mb-6">
          <h2 className="font-bold mb-2 text-gray-800">Output:</h2>
          <pre className="text-gray-900">{output}</pre>
        </div>
      )}

      {/* Voice Control */}
      <VoiceControl
        onCommand={(command) => {
          if (loading) return; // Prevent multiple commands at once
          if (command === "generate") handleGenerateCode();
          else if (command === "compile") handleCompile();
          else if (command === "save") handleSave();
        }}
      />
    </div>
  );
}
