import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import CodeEditor from "./CodeEditor";
import VoiceControl from "./VoiceControl";

function App() {
  const [transcript, setTranscript] = useState("");
  const [manualPrompt, setManualPrompt] = useState("");
  const [generatedCode, setGeneratedCode] = useState("");
  const [compileOutput, setCompileOutput] = useState("");
  const [loading, setLoading] = useState(false);
  const transcriptEndRef = useRef(null);
  const codeEndRef = useRef(null);

  // ðŸŽ™ Transcription (Whisper backend)
  const handleTranscribe = async (audioBlob) => {
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("file", audioBlob, "audio.wav");
      const res = await axios.post("http://localhost:5000/api/transcribe", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setTranscript(res.data.transcript || "");
    } catch (err) {
      console.error("Transcription failed:", err);
      setTranscript("âŒ Error transcribing audio.");
      speak("Error transcribing audio.");
    }
    setLoading(false);
  };

  // ðŸ¤– Code generation (Starcoder / Ollama backend)
  const handleGenerateCode = async () => {
    const promptToSend = transcript.trim() || manualPrompt.trim();
    if (!promptToSend) return;

    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/generate-code", {
        prompt: promptToSend,
      });
      const code = res.data.code || "No code generated.";
      setGeneratedCode(code);
      speak("Code generated successfully.");
    } catch (err) {
      console.error("Code generation failed:", err);
      setGeneratedCode("âŒ Error generating code.");
      speak("Error generating code.");
    }
    setLoading(false);
  };

  // â–¶ï¸ Compilation (Python, C, C++, Java)
  const handleCompile = async () => {
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/compile", {
        code: generatedCode,
        inputs: [],
      });
      const output = res.data.stdout || res.data.stderr || "âš ï¸ No output received.";
      setCompileOutput(output);
      speak("Compilation finished.");
    } catch (err) {
      console.error("Compilation error:", err);
      setCompileOutput("âŒ Error compiling code.");
      speak("Error compiling code.");
    }
    setLoading(false);
  };

  // ðŸ’¾ Save file locally
  const handleSaveFile = () => {
    const blob = new Blob([generatedCode], { type: "text/plain" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "generated_code.py";
    a.click();
    window.URL.revokeObjectURL(url);
    speak("File saved successfully.");
  };

  // ðŸŽ§ Voice feedback
  const speak = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    window.speechSynthesis.speak(utterance);
  };

  // ðŸ“œ Autoscroll transcript & code
  useEffect(() => {
    transcriptEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [transcript]);

  useEffect(() => {
    codeEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [generatedCode, compileOutput]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-gray-300 flex flex-col items-center">
      {/* Header */}
      <header className="w-full text-center py-8 bg-gradient-to-r from-purple-600 to-indigo-600 text-white shadow-lg">
        <h1 className="text-4xl font-bold">ðŸŽ™ Code with Your Voice</h1>
        <p className="mt-2 text-lg opacity-90">Code hands-free â€” your voice is now your IDE.</p>
      </header>

      {/* Main */}
      <main className="flex-1 w-full max-w-6xl p-6 grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Left: Voice + Prompt */}
        <div className="space-y-4">
          <VoiceControl onTranscribe={handleTranscribe} setTranscript={setTranscript} />

          <textarea
            className="w-full p-4 rounded-xl border shadow-sm focus:ring-2 focus:ring-indigo-500 focus:outline-none resize-none"
            rows="4"
            placeholder="Type your prompt here (if mic fails)..."
            value={manualPrompt}
            onChange={(e) => setManualPrompt(e.target.value)}
          />

          <button
            onClick={handleGenerateCode}
            disabled={loading}
            className="w-full bg-indigo-600 hover:bg-indigo-700 text-white py-2 px-4 rounded-xl shadow"
          >
            {loading ? "Generating..." : "Generate Code"}
          </button>
        </div>

        {/* Right: Code Editor + Output */}
        <div className="space-y-4">
          <div className="flex-1 flex flex-col border p-2 rounded bg-gray-50 overflow-y-auto h-64">
            <h2 className="font-semibold mb-2">Transcript</h2>
            <div className="flex-1 whitespace-pre-wrap">{transcript}</div>
            <div ref={transcriptEndRef}></div>
          </div>

          <CodeEditor
            generatedCode={generatedCode}
            setGeneratedCode={setGeneratedCode}
            handleCompile={handleCompile}
            handleSaveFile={handleSaveFile}
          />
          <div className="flex-1 flex flex-col border p-2 rounded bg-gray-900 text-green-400 overflow-y-auto h-48">
            <h2 className="font-semibold mb-2">Output</h2>
            <pre className="flex-1">{compileOutput}</pre>
            <div ref={codeEndRef}></div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
