import React, { useState, useRef, useEffect } from "react";
import axios from "axios";

function VoiceControl() {
  const [isRecording, setIsRecording] = useState(false);
  const [transcript, setTranscript] = useState("");
  const [manualPrompt, setManualPrompt] = useState("");
  const [generatedCode, setGeneratedCode] = useState("Your code will appear here.");
  const recognitionRef = useRef(null);

  // Play welcome message on first load
  useEffect(() => {
    const welcome = new Audio("/welcome.mp3"); // Put welcome.mp3 in public folder
    welcome.play().catch(err => console.log("Audio autoplay blocked:", err));
  }, []);

  // Start Speech Recognition
  const startListening = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Your browser does not support speech recognition.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = false;
    recognition.lang = "en-US";

    recognition.onresult = (event) => {
      const command = event.results[event.results.length - 1][0]
        .transcript.trim()
        .toLowerCase();
      console.log(`Heard: "${command}"`);

      setTranscript(command);

      // Voice commands
      if (command.includes("start recording")) {
        if (!isRecording) startListening();
      } else if (command.includes("stop recording")) {
        stopListening();
      } else if (command.includes("generate")) {
        handleGenerateCode(command);
      } else if (command.includes("scroll down")) {
        window.scrollBy(0, 200);
        console.log("Scrolled down");
      } else if (command.includes("scroll up")) {
        window.scrollBy(0, -200);
        console.log("Scrolled up");
      }
    };

    recognition.start();
    recognitionRef.current = recognition;
    setIsRecording(true);
    console.log("ðŸŽ¤ Started recording...");
  };

  // Stop Speech Recognition
  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      setIsRecording(false);
      console.log("ðŸ›‘ Stopped recording.");
    }
  };

  // Handle Generate Code
  const handleGenerateCode = async (promptText) => {
    const finalPrompt = promptText || manualPrompt || transcript;
    if (!finalPrompt) {
      alert("Please provide a prompt first.");
      return;
    }

    try {
      const res = await axios.post("http://localhost:5001/api/generate-code", {
        prompt: finalPrompt,
      });
      setGeneratedCode(res.data.code || "No code generated.");
    } catch (err) {
      console.error("Code generation failed:", err);
      setGeneratedCode("Error generating code. Check console.");
    }
  };

  return (
    <div className="p-4 border rounded">
      {/* Start/Stop Recording */}
      <button
        onClick={isRecording ? stopListening : startListening}
        className="bg-blue-500 text-white px-4 py-2 rounded"
      >
        {isRecording ? "Stop Recording" : "Start Recording"}
      </button>

      {/* Transcript */}
      <h3 className="mt-4">Transcript:</h3>
      <textarea
        value={transcript}
        onChange={(e) => setTranscript(e.target.value)}
        className="w-full border p-2 rounded"
        rows="2"
      />

      {/* Manual Prompt */}
      <h3 className="mt-4">Or Enter Prompt Manually:</h3>
      <input
        type="text"
        value={manualPrompt}
        onChange={(e) => setManualPrompt(e.target.value)}
        placeholder="Type your prompt if mic does not work..."
        className="w-full border p-2 rounded"
      />

      {/* Generate Code */}
      <button
        onClick={() => handleGenerateCode()}
        className="bg-green-500 text-white px-4 py-2 rounded mt-4"
      >
        âš¡ Generate Code
      </button>

      {/* Generated Code */}
      <h3 className="mt-4">ðŸ’» Generated Code:</h3>
      <pre className="bg-gray-100 p-4 rounded border whitespace-pre-wrap">
        {generatedCode}
      </pre>
    </div>
  );
}

export default VoiceControl;
