import React, { useState } from "react";

export default function App() {
  const [transcript, setTranscript] = useState("");
  const [manualPrompt, setManualPrompt] = useState("");
  const [generatedCode, setGeneratedCode] = useState("");
  const [compileOutput, setCompileOutput] = useState("");
  const [recording, setRecording] = useState(false);

  const handleStartRecording = () => {
    setRecording(true);
    setTimeout(() => {
      setTranscript("print('Hello World')"); // example
      setRecording(false);
    }, 1500);
  };

  const handleGenerateCode = async () => {
    const promptToSend = transcript || manualPrompt;
    if (!promptToSend.trim()) return alert("Please provide a prompt");

    const res = await fetch("http://localhost:5000/api/generate-code", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ prompt: promptToSend }),
    });

    const data = await res.json();
    setGeneratedCode(data.code || "// No code generated");
  };

  const handleCompile = async () => {
    if (!generatedCode.trim()) return alert("No code to compile");

    const res = await fetch("http://localhost:5000/api/compile", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code: generatedCode }),
    });

    const data = await res.json();
    setCompileOutput(
      data.output || data.errors || "// No output or errors returned"
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-indigo-50 via-white to-purple-50 flex flex-col items-center">

      {/* Header */}
      <header className="w-full text-center py-8 bg-gradient-to-r from-purple-500 to-indigo-500 text-white shadow-lg">
        <h1 className="text-3xl font-bold">Code with Your Voice</h1>
        <p className="mt-2 text-lg opacity-90">
          Because ideas should never wait for hands to type
        </p>
      </header>

      {/* Main Container */}
      <div className="w-full max-w-5xl p-6 space-y-6">

        {/* Mic Button */}
        <div className="flex flex-col items-center">
          <button
            onClick={handleStartRecording}
            className={`w-24 h-24 rounded-full flex items-center justify-center shadow-xl transition-all ${
              recording
                ? "bg-red-500 animate-pulse"
                : "bg-green-500 hover:bg-green-600"
            }`}
          >
            {recording ? "ðŸŽ™" : "ðŸŽ¤"}
          </button>
          <p className="mt-3 text-gray-600">
            {recording ? "Listening..." : "Click to start speaking"}
          </p>
        </div>

        {/* Transcript + Manual Input */}
        <div className="bg-white shadow rounded-lg p-4 space-y-4">
          <textarea
            value={transcript}
            onChange={(e) => setTranscript(e.target.value)}
            placeholder="Transcript will appear here..."
            rows={2}
            className="w-full border rounded p-2 focus:outline-none focus:ring-2 focus:ring-purple-400"
          />
          <input
            type="text"
            value={manualPrompt}
            onChange={(e) => setManualPrompt(e.target.value)}
            placeholder="Or type your prompt..."
            className="w-full border rounded p-2 focus:outline-none focus:ring-2 focus:ring-purple-400"
          />
        </div>

        {/* Generate Code */}
        <div className="bg-white shadow rounded-lg p-4 space-y-4">
          <button
            onClick={handleGenerateCode}
            className="px-6 py-2 bg-purple-600 hover:bg-purple-700 text-white font-semibold rounded shadow"
          >
            âš¡ Generate Code
          </button>
          <pre className="bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto min-h-[150px]">
            {generatedCode || "// Your generated code will appear here"}
          </pre>
        </div>

        {/* Compile & Output */}
        <div className="bg-white shadow rounded-lg p-4 space-y-4">
          <button
            onClick={handleCompile}
            className="px-6 py-2 bg-indigo-600 hover:bg-indigo-700 text-white font-semibold rounded shadow"
          >
            ðŸ›  Compile & Run
          </button>
          <pre className="bg-black text-white p-4 rounded-lg overflow-x-auto min-h-[100px]">
            {compileOutput || "// Output will appear here"}
          </pre>
        </div>
      </div>
    </div>
  );
}
