import React, { useState } from "react";

export default function App() {
  const [transcript, setTranscript] = useState("");
  const [manualPrompt, setManualPrompt] = useState("");
  const [generatedCode, setGeneratedCode] = useState("");
  const [compileOutput, setCompileOutput] = useState("");
  const [recording, setRecording] = useState(false);
  const [loadingGenerate, setLoadingGenerate] = useState(false);
  const [loadingCompile, setLoadingCompile] = useState(false);

  // üé§ Start recording & send to backend
  const handleStartRecording = async () => {
    setRecording(true);
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      const chunks = [];

      mediaRecorder.ondataavailable = (e) => chunks.push(e.data);
      mediaRecorder.onstop = async () => {
        const blob = new Blob(chunks, { type: "audio/wav" });
        const formData = new FormData();
        formData.append("file", blob, "recording.wav");

        const res = await fetch("http://localhost:5000/api/transcribe", {
          method: "POST",
          body: formData,
        });
        const data = await res.json();
        setTranscript(data.transcript || "");
        setRecording(false);
      };

      mediaRecorder.start();
      setTimeout(() => mediaRecorder.stop(), 4000); // Auto-stop after 4s
    } catch (err) {
      console.error(err);
      alert("Could not access microphone.");
      setRecording(false);
    }
  };

  const handleGenerateCode = async () => {
    const promptToSend = transcript || manualPrompt;
    if (!promptToSend.trim()) return alert("Please provide a prompt");
    setLoadingGenerate(true);
    setGeneratedCode("");
    try {
      const res = await fetch("http://localhost:5000/api/generate-code", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt: promptToSend }),
      });
      const data = await res.json();
      setGeneratedCode(data.code || "// No code generated");
    } catch {
      alert("Error generating code.");
    }
    setLoadingGenerate(false);
  };

  const handleCompile = async () => {
    if (!generatedCode.trim()) return alert("No code to compile");
    setLoadingCompile(true);
    setCompileOutput("");
    try {
      const res = await fetch("http://localhost:5000/api/compile", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code: generatedCode }),
      });
      const data = await res.json();
      setCompileOutput(
        data.stdout || data.stderr || "// No output or errors returned"
      );
    } catch {
      alert("Error compiling code.");
    }
    setLoadingCompile(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-indigo-100 via-white to-purple-100 flex flex-col items-center">
      {/* Header */}
      <header className="w-full text-center py-8 bg-gradient-to-r from-purple-600 to-indigo-600 text-white shadow-lg">
        <h1 className="text-4xl font-bold">üéô Code with Your Voice</h1>
        <p className="mt-2 text-lg opacity-90">
          No keyboard? No problem ‚Äî your voice is the new IDE.
        </p>
      </header>

      {/* Main Container */}
      <div className="w-full max-w-5xl p-6 space-y-6">
        {/* Mic Button */}
        <div className="flex flex-col items-center">
          <button
            onClick={handleStartRecording}
            disabled={recording}
            className={`w-28 h-28 rounded-full flex items-center justify-center shadow-xl text-4xl transition-all focus:outline-none focus:ring-4 ${
              recording
                ? "bg-red-500 animate-pulse focus:ring-red-300"
                : "bg-green-500 hover:bg-green-600 focus:ring-green-300"
            }`}
          >
            {recording ? "üéô" : "üé§"}
          </button>
          <p className="mt-3 text-gray-700 text-lg">
            {recording ? "Listening..." : "Click to speak your code idea"}
          </p>
        </div>

        {/* Transcript + Manual Input */}
        <div className="bg-white shadow rounded-lg p-4 space-y-4">
          <textarea
            value={transcript}
            onChange={(e) => setTranscript(e.target.value)}
            placeholder="Transcript will appear here..."
            rows={2}
            className="w-full border rounded p-3 text-lg focus:outline-none focus:ring-2 focus:ring-purple-400"
          />
          <input
            type="text"
            value={manualPrompt}
            onChange={(e) => setManualPrompt(e.target.value)}
            placeholder="Or type your prompt..."
            className="w-full border rounded p-3 text-lg focus:outline-none focus:ring-2 focus:ring-purple-400"
          />
        </div>

        {/* Generate Code */}
        <div className="bg-white shadow rounded-lg p-4 space-y-4">
          <button
            onClick={handleGenerateCode}
            disabled={loadingGenerate}
            className="px-6 py-3 bg-purple-600 hover:bg-purple-700 disabled:opacity-50 text-white text-lg font-semibold rounded shadow"
          >
            {loadingGenerate ? "‚è≥ Generating..." : "‚ö° Generate Code"}
          </button>
          <pre className="bg-gray-900 text-green-400 p-4 rounded-lg overflow-x-auto min-h-[150px] text-sm">
            {generatedCode || "// Your generated code will appear here"}
          </pre>
        </div>

        {/* Compile & Output */}
        <div className="bg-white shadow rounded-lg p-4 space-y-4">
          <button
            onClick={handleCompile}
            disabled={loadingCompile}
            className="px-6 py-3 bg-indigo-600 hover:bg-indigo-700 disabled:opacity-50 text-white text-lg font-semibold rounded shadow"
          >
            {loadingCompile ? "üõ† Compiling..." : "üõ† Compile & Run"}
          </button>
          <pre className="bg-black text-white p-4 rounded-lg overflow-x-auto min-h-[100px] text-sm">
            {compileOutput || "// Output will appear here"}
          </pre>
        </div>
      </div>
    </div>
  );
}
