import React, { useRef, useState } from "react";
import axios from "axios";

function MicInput({ setTranscript, transcript, setGeneratedCode }) {
  const [isRecording, setIsRecording] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    mediaRecorderRef.current = new MediaRecorder(stream);
    audioChunksRef.current = [];

    mediaRecorderRef.current.ondataavailable = (event) => {
      if (event.data.size > 0) {
        audioChunksRef.current.push(event.data);
      }
    };

    mediaRecorderRef.current.onstop = async () => {
      const audioBlob = new Blob(audioChunksRef.current, { type: "audio/webm" });
      const formData = new FormData();
      formData.append("audio", audioBlob, "recording.webm");

      try {
        const res = await axios.post("http://localhost:5000/api/transcribe", formData);
        const text = res.data.transcript;
        setTranscript(text);
      } catch (err) {
        console.error("Transcription failed:", err);
      }
    };

    mediaRecorderRef.current.start();
    setIsRecording(true);
  };

  const stopRecording = () => {
    mediaRecorderRef.current.stop();
    setIsRecording(false);
  };

  const handleGenerateCode = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/parse", {
        command: transcript,
      });
      setGeneratedCode(res.data.code);
    } catch (err) {
      console.error("Code generation failed:", err);
    }
  };

  return (
    <div className="mb-4">
      <button
        onClick={isRecording ? stopRecording : startRecording}
        className={`px-4 py-2 mr-3 rounded text-white ${
          isRecording ? "bg-red-500" : "bg-green-600"
        }`}
      >
        {isRecording ? "Stop Recording" : "ðŸŽ¤ Start Recording"}
      </button>

      <button
        onClick={handleGenerateCode}
        className="px-4 py-2 bg-blue-600 text-white rounded"
        disabled={!transcript}
      >
        ðŸ›  Generate Code
      </button>
    </div>
  );
}

export default MicInput;
