// App.jsx
import React, { useState } from "react";
import VoiceControl from "./VoiceControl"; // Your voice component

const API_BASE_URL = "http://localhost:5000/api";

export default function App() {
  const [manualPrompt, setManualPrompt] = useState("");
  const [generatedCode, setGeneratedCode] = useState("");
  const [userInput, setUserInput] = useState("");
  const [output, setOutput] = useState("");
  const [errors, setErrors] = useState("");
  const [loading, setLoading] = useState(false);

  const handleGenerateCode = async (prompt) => {
    setLoading(true);
    setErrors("");
    setOutput("");

    try {
      const res = await fetch(`${API_BASE_URL}/generate-code`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt }),
      });

      const data = await res.json();
      if (res.ok) {
        setGeneratedCode(data.code || "");
      } else {
        setErrors(data.error || "Unknown error");
      }
    } catch (err) {
      setErrors(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleRunCode = async () => {
    setLoading(true);
    setOutput("");
    setErrors("");

    try {
      const res = await fetch(`${API_BASE_URL}/compile`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code: generatedCode, input: userInput }),
      });

      const data = await res.json();
      if (res.ok) {
        setOutput(data.output || "");
        setErrors(data.errors || "");
      } else {
        setErrors(data.error || "Unknown error");
      }
    } catch (err) {
      setErrors(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSaveFile = () => {
    const blob = new Blob([generatedCode], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "generated_code.py";
    a.click();
    URL.revokeObjectURL(url);
  };

  // Called from VoiceControl.jsx when a command is recognized
  const handleVoiceCommand = (command) => {
    const cmd = command.toLowerCase();
    if (cmd.includes("generate code")) {
      handleGenerateCode(manualPrompt || command.replace("generate code", "").trim());
    } else if (cmd.includes("compile")) {
      handleRunCode();
    } else if (cmd.includes("save file")) {
      handleSaveFile();
    } else {
      setManualPrompt(command);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <VoiceControl onCommand={handleVoiceCommand} />

      <h3>Transcript:</h3>
      <textarea
        value={manualPrompt}
        onChange={(e) => setManualPrompt(e.target.value)}
        placeholder="Type your prompt..."
        rows={3}
        style={{ width: "100%", marginBottom: "10px" }}
      />

      <div style={{ display: "flex", gap: "10px" }}>
        <button onClick={() => handleGenerateCode(manualPrompt)}>Generate Code</button>
        <button onClick={handleRunCode} disabled={!generatedCode}>Compile</button>
        <button onClick={handleSaveFile} disabled={!generatedCode}>Save</button>
      </div>

      {generatedCode && (
        <>
          <h3>Generated Code:</h3>
          <pre style={{ background: "#f5f5f5", padding: "10px" }}>{generatedCode}</pre>

          <h4>Program Input:</h4>
          <textarea
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            rows={3}
            style={{ width: "100%", marginBottom: "10px" }}
          />
        </>
      )}

      {output && (
        <>
          <h3>Output:</h3>
          <pre style={{ background: "#eaffea", padding: "10px" }}>{output}</pre>
        </>
      )}

      {errors && (
        <>
          <h3 style={{ color: "red" }}>Errors:</h3>
          <pre style={{ background: "#ffeaea", padding: "10px" }}>{errors}</pre>
        </>
      )}
    </div>
  );
}
