import React, { useRef, useState } from 'react';
import axios from 'axios';

function MicInput() {
  const [isRecording, setIsRecording] = useState(false);
  const [transcript, setTranscript] = useState('');
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    mediaRecorderRef.current = new MediaRecorder(stream);
    audioChunksRef.current = [];

    mediaRecorderRef.current.ondataavailable = (event) => {
      if (event.data.size > 0) {
        audioChunksRef.current.push(event.data);
      }
    };

    mediaRecorderRef.current.onstop = async () => {
      const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
      const formData = new FormData();
      formData.append('audio', audioBlob, 'recording.webm');

      try {
        const response = await axios.post('http://localhost:5001/api/transcribe', formData);
        const text = response.data.transcript;
        setTranscript(text);

        // Send to backend for code generation
        const genResponse = await axios.post('http://localhost:5000/api/parse', {
          command: text,
        });

        const code = genResponse.data.code;
        localStorage.setItem('generatedCode', code);
        window.location.reload(); // To re-render updated code in CodeEditor

      } catch (err) {
        console.error('Transcription or code generation failed:', err);
      }
    };

    mediaRecorderRef.current.start();
    setIsRecording(true);
  };

  const stopRecording = () => {
    mediaRecorderRef.current.stop();
    setIsRecording(false);
  };

  return (
    <div className="flex items-center justify-center mb-4">
      <button
        onClick={isRecording ? stopRecording : startRecording}
        className={`px-4 py-2 rounded text-white ${isRecording ? 'bg-red-500' : 'bg-green-600'}`}
      >
        {isRecording ? 'Stop Recording' : 'Start Recording'}
      </button>
    </div>
  );
}

export default MicInput;
