import React, { useEffect, useRef, useState } from "react";
import axios from "axios";

const VoiceControl = ({ setTranscript, setGeneratedCode }) => {
  const recognitionRef = useRef(null);
  const [mode, setMode] = useState("command");
  const [isRecording, setIsRecording] = useState(false);

  const synth = window.speechSynthesis;
  const speak = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    synth.speak(utterance);
  };

  const startRecording = () => {
    setIsRecording(true);
    setMode("input");
    speak("Please say your prompt to generate code.");
  };

  const stopRecording = () => {
    setIsRecording(false);
    setMode("command");
    speak("Stopped recording.");
  };

  const handleCommand = async (command) => {
    const lower = command.toLowerCase();

    if (lower.includes("start recording")) {
      startRecording(); // ðŸ”¹ Now works like your button
    } else if (lower.includes("stop recording")) {
      stopRecording();
    } else if (lower.includes("generate")) {
      startRecording(); // "generate" now also starts recording prompt
    } else if (lower.includes("compile")) {
      speak("Compiling your code.");
      try {
        const res = await axios.post("http://localhost:5001/api/compile", {
          code: "" // Send your code if needed
        });
        const output = res.data.output || res.data.errors || "No output.";
        console.log("ðŸ› ï¸ Compile Output:", output);
        speak("Compilation complete. Check output.");
      } catch (err) {
        console.error("Compile error:", err);
        speak("Error compiling the code.");
      }
    } else if (lower.includes("save")) {
      speak("Saving your code.");
      try {
        await axios.post("http://localhost:5001/api/save", {
          code: "" // Add save logic later
        });
        speak("Code saved successfully.");
      } catch (err) {
        console.error("Save error:", err);
        speak("Error saving the code.");
      }
    } else if (lower.includes("scroll up")) {
      window.scrollBy({ top: -200, behavior: "smooth" });
    } else if (lower.includes("scroll down")) {
      window.scrollBy({ top: 200, behavior: "smooth" });
    } else {
      speak("I did not understand that command.");
    }
  };

  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Speech Recognition not supported");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = false;
    recognition.lang = "en-US";
    recognitionRef.current = recognition;

    recognition.onresult = async (event) => {
      const result = event.results[event.results.length - 1][0].transcript.trim().toLowerCase();
      console.log(`ðŸŽ¤ Heard: "${result}" | Mode: ${mode}`);

      if (mode === "command") {
        handleCommand(result);
      } else if (mode === "input") {
        setTranscript(result);
        speak("Generating code now.");
        setMode("command");
        setIsRecording(false);

        try {
          const res = await axios.post("http://localhost:5001/api/parse", {
            prompt: result,
          });
          const code = res.data.code || "// No code generated.";
          setGeneratedCode(code);
          console.log("ðŸ’¡ Generated code:", code);
        } catch (err) {
          console.error("Code generation failed:", err);
          speak("Something went wrong generating the code.");
        }
      }
    };

    recognition.onerror = (event) => {
      console.error("Recognition error:", event.error);
      speak("Voice recognition error.");
    };

    recognition.start();

    // Play welcome audio on load
    const welcomeAudio = new Audio("/welcome.mp3");
    welcomeAudio.play().catch(() => {
      // If autoplay blocked, fallback to speech synthesis
      speak("Welcome to CodeVoice. Speak your wish and we will generate a code for you.");
    });

    return () => recognition.stop();
  }, [mode, setTranscript, setGeneratedCode]);

  return (
    <div className="text-center mb-4">
      <p className="text-lg text-gray-700">
        ðŸŽ§ Try saying: "start recording", "stop recording", "generate", "compile", "save", "scroll up", "scroll down"
      </p>
      <p className="text-sm text-blue-500">
        ðŸŽ¤ Current mode: {mode === "command" ? "Listening for commands" : "Listening for prompt"}
      </p>
      {isRecording && <p className="text-red-500">ðŸ”´ Recording...</p>}
    </div>
  );
};

export default VoiceControl;
