import React, { useState, useRef } from "react";
import axios from "axios";

function App() {
  const [transcript, setTranscript] = useState("");
  const [manualPrompt, setManualPrompt] = useState("");
  const [generatedCode, setGeneratedCode] = useState("");
  const [compileOutput, setCompileOutput] = useState("");
  const [loading, setLoading] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // üé§ Handle Mic Input
  const handleMicClick = async () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
      return;
    }

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream, { mimeType: "audio/webm" });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: "audio/webm" });
        const formData = new FormData();
        formData.append("file", audioBlob, "recording.webm");

        try {
          setLoading(true);
          const res = await axios.post("http://localhost:8000/api/transcribe", formData, {
            headers: { "Content-Type": "multipart/form-data" },
          });
          setTranscript(res.data.text || "");
        } catch (err) {
          console.error("Transcription failed:", err);
          alert("Failed to transcribe audio.");
        } finally {
          setLoading(false);
        }
      };

      mediaRecorder.start();
    } catch (err) {
      console.error("Mic access error:", err);
      alert("Microphone not accessible!");
    }
  };

  // üìù Generate Code
  const handleGenerateCode = async () => {
    const finalPrompt = transcript || manualPrompt;
    if (!finalPrompt) {
      alert("Please provide input (mic or manual).");
      return;
    }

    try {
      setLoading(true);
      const res = await axios.post("http://localhost:8000/api/generate-code", {
        prompt: finalPrompt,
      });
      setGeneratedCode(res.data.code || "");
    } catch (err) {
      console.error("Code generation failed:", err);
      alert("Failed to generate code.");
    } finally {
      setLoading(false);
    }
  };

  // ‚ñ∂Ô∏è Compile Code
  const handleCompile = async () => {
    if (!generatedCode) {
      alert("No code to compile.");
      return;
    }

    try {
      setLoading(true);
      const res = await axios.post("http://localhost:8000/api/compile", {
        code: generatedCode,
      });
      setCompileOutput(res.data.output || "");
    } catch (err) {
      console.error("Compilation failed:", err);
      alert("Failed to compile code.");
    } finally {
      setLoading(false);
    }
  };

  // üíæ Save File
  const handleSaveFile = () => {
    if (!generatedCode) {
      alert("No code to save.");
      return;
    }
    const blob = new Blob([generatedCode], { type: "text/plain" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "generated_code.py"; // default extension
    link.click();
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center p-6">
      <div className="w-full max-w-2xl bg-gray-800 p-6 rounded-2xl shadow-lg">
        <h1 className="text-2xl font-bold mb-6 text-center">üéôÔ∏è CodeVoice</h1>

        {/* Transcript Box */}
        <textarea
          value={transcript}
          onChange={(e) => setTranscript(e.target.value)}
          placeholder="Transcript will appear here..."
          className="w-full p-3 mb-4 rounded-lg bg-gray-700 text-white"
        />

        {/* Manual Prompt Input */}
        <input
          type="text"
          value={manualPrompt}
          onChange={(e) => setManualPrompt(e.target.value)}
          placeholder="Or type your prompt here..."
          className="w-full p-3 mb-4 rounded-lg bg-gray-700 text-white"
        />

        {/* Buttons */}
        <div className="flex flex-col space-y-3">
          <button
            onClick={handleMicClick}
            className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg shadow"
          >
            üé§ {mediaRecorderRef.current?.state === "recording" ? "Stop Recording" : "Record"}
          </button>

          <button
            onClick={handleGenerateCode}
            disabled={loading}
            className="w-full bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-lg shadow"
          >
            ‚ö° Generate Code
          </button>

          <button
            onClick={handleCompile}
            disabled={!generatedCode || loading}
            className="w-full bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg shadow"
          >
            ‚ñ∂Ô∏è Compile Code
          </button>

          {/* Save File */}
          <button
            onClick={handleSaveFile}
            disabled={!generatedCode}
            className="w-full bg-gray-700 hover:bg-gray-800 text-white py-2 px-4 rounded-lg shadow"
          >
            üíæ Save Code
          </button>
        </div>

        {/* Generated Code */}
        {generatedCode && (
          <div className="mt-6">
            <h2 className="text-lg font-semibold mb-2">üìù Generated Code:</h2>
            <pre className="bg-gray-900 p-4 rounded-lg overflow-x-auto text-green-400">
              {generatedCode}
            </pre>
          </div>
        )}

        {/* Compile Output */}
        {compileOutput && (
          <div className="mt-6">
            <h2 className="text-lg font-semibold mb-2">üíª Output:</h2>
            <pre className="bg-gray-900 p-4 rounded-lg overflow-x-auto text-yellow-300">
              {compileOutput}
            </pre>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
