import React, { useState, useRef, useEffect } from "react";
import axios from "axios";

function App() {
  const [transcript, setTranscript] = useState("");
  const [manualPrompt, setManualPrompt] = useState("");
  const [generatedCode, setGeneratedCode] = useState("");
  const [compileOutput, setCompileOutput] = useState("");
  const [loading, setLoading] = useState(false);
  const mediaRecorderRef = useRef(null);
  const [recording, setRecording] = useState(false);

  // Speech synthesis helper
  const speak = (text) => {
    if ("speechSynthesis" in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      window.speechSynthesis.speak(utterance);
    }
  };

  // Handle microphone recording
  const handleMicClick = async () => {
    if (!recording) {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const mediaRecorder = new MediaRecorder(stream, { mimeType: "audio/webm" });
        mediaRecorderRef.current = mediaRecorder;
        const chunks = [];

        mediaRecorder.ondataavailable = (e) => {
          if (e.data.size > 0) chunks.push(e.data);
        };

        mediaRecorder.onstop = async () => {
          const blob = new Blob(chunks, { type: "audio/webm" });
          const formData = new FormData();
          formData.append("file", blob, "recording.webm");

          try {
            const res = await axios.post("http://localhost:8000/transcribe/", formData, {
              headers: { "Content-Type": "multipart/form-data" },
            });

            if (res.data.transcript) {
              setTranscript((prev) => prev + " " + res.data.transcript);
            } else if (res.data.error) {
              setTranscript("‚ùå " + res.data.error);
              speak("Error transcribing audio.");
            } else {
              setTranscript("‚ùå No transcript received.");
              speak("No transcript received.");
            }
          } catch (err) {
            console.error("Transcription error:", err);
            setTranscript("‚ùå Error sending audio to server.");
            speak("Error sending audio to server.");
          }
        };

        mediaRecorder.start();
        setRecording(true);
      } catch (err) {
        console.error("Mic error:", err);
        speak("Microphone access error.");
      }
    } else {
      if (mediaRecorderRef.current) {
        mediaRecorderRef.current.stop();
      }
      setRecording(false);
    }
  };

  // Handle manual audio file transcription
  const handleTranscribe = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post("http://localhost:8000/transcribe/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (res.data.transcript) {
        setTranscript(res.data.transcript);
      } else if (res.data.error) {
        setTranscript("‚ùå " + res.data.error);
        speak("Error transcribing audio.");
      } else {
        setTranscript("‚ùå No transcript received.");
        speak("No transcript received.");
      }
    } catch (err) {
      console.error("Transcription error:", err);
      setTranscript("‚ùå Error sending file to server.");
      speak("Error sending file to server.");
    }
  };

  // Handle code generation
  const handleGenerateCode = async () => {
    if (!manualPrompt && !transcript) {
      speak("Please provide input first.");
      return;
    }

    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/generate-code", {
        prompt: manualPrompt || transcript,
      });

      if (res.data.code) {
        setGeneratedCode(res.data.code);
        speak("Code generated successfully.");
      } else {
        setGeneratedCode("‚ùå Error: No code received.");
        speak("Error generating code.");
      }
    } catch (err) {
      console.error("Code generation error:", err);
      setGeneratedCode("‚ùå Error generating code.");
      speak("Error generating code.");
    }
    setLoading(false);
  };

  // Handle compilation
  const handleCompile = async () => {
    if (!generatedCode) {
      speak("Please generate code first.");
      return;
    }

    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/compile", {
        code: generatedCode,
        language: "python",
      });

      if (res.data.output !== undefined) {
        setCompileOutput(res.data.output);
        speak("Code compiled successfully.");
      } else {
        setCompileOutput("‚ùå Error: No output received.");
        speak("Error during compilation.");
      }
    } catch (err) {
      console.error("Compilation error:", err);
      setCompileOutput("‚ùå Error compiling code.");
      speak("Error compiling code.");
    }
    setLoading(false);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4 space-y-6 bg-gray-100">
      <h1 className="text-3xl font-bold">üé§ CodeVoice</h1>

      {/* Mic + File Upload */}
      <div className="flex space-x-4">
        <button
          onClick={handleMicClick}
          className={`px-4 py-2 rounded-lg text-white ${
            recording ? "bg-red-500" : "bg-blue-500"
          }`}
        >
          {recording ? "‚èπ Stop Recording" : "üéô Start Recording"}
        </button>

        <input
          type="file"
          accept="audio/*"
          onChange={handleTranscribe}
          className="px-4 py-2 border rounded-lg"
        />
      </div>

      {/* Transcript */}
      <textarea
        value={transcript}
        onChange={(e) => setTranscript(e.target.value)}
        placeholder="Transcript will appear here..."
        className="w-full max-w-2xl h-24 p-2 border rounded-lg"
      />

      {/* Manual Prompt */}
      <textarea
        value={manualPrompt}
        onChange={(e) => setManualPrompt(e.target.value)}
        placeholder="Enter your prompt here..."
        className="w-full max-w-2xl h-24 p-2 border rounded-lg"
      />

      {/* Buttons */}
      <div className="flex space-x-4">
        <button
          onClick={handleGenerateCode}
          disabled={loading}
          className="px-4 py-2 bg-green-500 text-white rounded-lg"
        >
          ‚ö° Generate Code
        </button>
        <button
          onClick={handleCompile}
          disabled={loading}
          className="px-4 py-2 bg-purple-500 text-white rounded-lg"
        >
          üõ† Compile
        </button>
      </div>

      {/* Generated Code */}
      <pre className="w-full max-w-2xl p-2 bg-white border rounded-lg overflow-x-auto whitespace-pre-wrap">
        {generatedCode || "Generated code will appear here..."}
      </pre>

      {/* Compile Output */}
      <pre className="w-full max-w-2xl p-2 bg-gray-200 border rounded-lg overflow-x-auto whitespace-pre-wrap">
        {compileOutput || "Compilation output will appear here..."}
      </pre>
    </div>
  );
}

export default App;
