import React, { useState } from 'react';
import CodeEditor from './components/Editor';
import MicInput from './components/MicInput';
import axios from 'axios';

function App() {
  const [code, setCode] = useState('');

  const handleVoiceCommand = async (spokenText) => {
    console.log("Voice Input:", spokenText);
    try {
      const response = await axios.post('http://localhost:5000/api/parse', { text: spokenText });
      if (response.data?.code) {
        setCode((prev) => prev + '\n' + response.data.code);
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Backend error: " + error.message);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 to-black text-white font-sans px-4 py-6 flex flex-col items-center">

      {/* Header Section */}
      <header className="w-full bg-gradient-to-r from-indigo-600 to-blue-600 py-5 rounded-xl shadow-md mb-6">
        <h1 className="text-4xl font-extrabold text-center tracking-wide text-white">üß† CodeVoice</h1>
        <p className="text-center text-white text-sm">Your Voice-Controlled Coding Companion</p>
      </header>

      {/* Mic Input Section */}
      <section className="bg-gray-800 p-6 rounded-2xl shadow-2xl text-center max-w-md w-full">
        <h2 className="text-xl font-semibold text-white mb-3">Start Speaking</h2>
        <MicInput onTranscript={handleVoiceCommand} />
        <p className="text-sm text-gray-400 mt-3 italic">Say something like ‚Äúcreate a for loop‚Äù</p>
      </section>

      {/* Code Editor Section */}
      <section className="mt-8 w-full max-w-6xl backdrop-blur-lg bg-white/5 border border-gray-700 rounded-2xl shadow-2xl overflow-hidden">
        <CodeEditor code={code} setCode={setCode} />
      </section>

      {/* Footer */}
      <footer className="mt-10 text-gray-500 text-sm text-center">
        ¬© 2025 CodeVoice ‚Ä¢ Built with React, Tailwind CSS & Monaco Editor
      </footer>
    </div>
  );
}

export default App;
