// src/components/VoiceControl.jsx
import React, { useEffect, useRef, useState } from "react";

const VoiceControl = ({ onGenerate, onCompile, onSave }) => {
  const [isListening, setIsListening] = useState(false);
  const recognitionRef = useRef(null);
  const streamRef = useRef(null);

  useEffect(() => {
    // Setup SpeechRecognition
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Your browser does not support Speech Recognition.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = true;
    recognition.lang = "en-US";
    recognition.interimResults = false;

    recognition.onresult = (event) => {
      const transcript = event.results[event.results.length - 1][0].transcript
        .trim()
        .toLowerCase();
      console.log("Heard:", transcript);

      if (transcript.includes("generate code")) {
        onGenerate();
      } else if (transcript.includes("compile code")) {
        onCompile();
      } else if (transcript.includes("save code")) {
        onSave();
      }
    };

    recognitionRef.current = recognition;

    // Cleanup function
    return () => {
      stopListening();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const startListening = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      streamRef.current = stream;
      recognitionRef.current.start();
      setIsListening(true);
    } catch (err) {
      console.error("Mic access denied:", err);
    }
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop()); // Release mic
    }
    setIsListening(false);
  };

  return (
    <div className="flex items-center gap-4">
      <button
        onClick={isListening ? stopListening : startListening}
        className={`px-4 py-2 rounded ${
          isListening ? "bg-red-500" : "bg-green-500"
        } text-white`}
      >
        {isListening ? "Stop Voice Control" : "Start Voice Control"}
      </button>
    </div>
  );
};

export default VoiceControl;
