import React, { useEffect, useState } from "react";

function App() {
  const [transcript, setTranscript] = useState("");
  const [recognition, setRecognition] = useState(null);

  // Voice greeting when app loads
  useEffect(() => {
    const speak = (text) => {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = "en-US";
      window.speechSynthesis.speak(utterance);
    };
    speak("Welcome to CodeVoice. You can say generate, compile, or save to control the app.");

    // Set up voice recognition
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recog = new SpeechRecognition();
      recog.continuous = true;
      recog.interimResults = false;
      recog.lang = "en-US";

      recog.onresult = (event) => {
        const lastResult = event.results[event.results.length - 1][0].transcript
          .trim()
          .toLowerCase();
        setTranscript(lastResult);
        handleVoiceCommand(lastResult);
      };

      recog.start();
      setRecognition(recog);
    } else {
      alert("Speech Recognition not supported in this browser.");
    }
  }, []);

  // Handle voice commands
  const handleVoiceCommand = (command) => {
    if (command.includes("generate")) {
      alert("Generating code...");
      // call your /api/generate-code endpoint here
    } else if (command.includes("compile")) {
      alert("Compiling code...");
      // call your /api/compile endpoint here
    } else if (command.includes("save")) {
      alert("Saving file...");
      // trigger save function here
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>CodeVoice</h1>
      <p>Say: <b>generate</b>, <b>compile</b>, or <b>save</b></p>
      <div style={{ marginTop: "20px", padding: "10px", border: "1px solid #ccc" }}>
        <strong>Last Command:</strong> {transcript}
      </div>
    </div>
  );
}

export default App;
