import React, { useState, useRef } from "react";
import axios from "axios";

function App() {
  const [transcript, setTranscript] = useState("");
  const [manualPrompt, setManualPrompt] = useState("");
  const [generatedCode, setGeneratedCode] = useState("");
  const [compileOutput, setCompileOutput] = useState("");
  const [loading, setLoading] = useState(false);
  const [language, setLanguage] = useState("python");
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // 🎤 Handle microphone recording
  const handleMicClick = async () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
      return;
    }

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream, { mimeType: "audio/webm" });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: "audio/webm" });
        const formData = new FormData();
        formData.append("file", audioBlob, "recording.webm");

        try {
          const res = await axios.post("http://localhost:5000/api/transcribe", formData, {
            headers: { "Content-Type": "multipart/form-data" },
          });
          setTranscript(res.data.transcript || "");
        } catch (err) {
          console.error("Transcription error:", err);
          setTranscript("⚠️ Error: Could not transcribe audio.");
        }
      };

      mediaRecorder.start();
    } catch (err) {
      console.error("Microphone access error:", err);
      alert("⚠️ Please allow microphone access to record.");
    }
  };

  // 📤 Upload file for transcription
  const handleTranscribe = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post("http://localhost:5000/api/transcribe", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setTranscript(res.data.transcript || "");
    } catch (err) {
      console.error("File transcription error:", err);
      setTranscript("⚠️ Error: Could not transcribe file.");
    }
  };

  // 🤖 Generate code
  const handleGenerateCode = async () => {
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/generate-code", {
        prompt: transcript || manualPrompt,
        language,
      });
      setGeneratedCode(res.data.code || "");
    } catch (err) {
      console.error("Code generation error:", err);
      setGeneratedCode("⚠️ Error: Could not generate code.");
    } finally {
      setLoading(false);
    }
  };

  // ▶️ Compile code
  const handleCompile = async () => {
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/compile", {
        code: generatedCode,
        language,
      });
      setCompileOutput(res.data.output || "");
    } catch (err) {
      console.error("Compilation error:", err);
      setCompileOutput("⚠️ Error: Could not compile code.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-indigo-100 via-white to-indigo-200">
      {/* Banner */}
      <header className="w-full py-4 bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 text-white text-center shadow-md">
        <h1 className="text-3xl font-bold tracking-wide">🎤 CodeVoice</h1>
        <p className="text-sm opacity-90">Your Voice-Controlled Coding Assistant</p>
      </header>

      {/* Controls */}
      <div className="flex flex-col items-center mt-6 space-y-4 w-full max-w-2xl">
        {/* Mic Button */}
        <button
          onClick={handleMicClick}
          className="bg-red-500 hover:bg-red-600 text-white rounded-full w-20 h-20 flex items-center justify-center shadow-lg text-xl font-bold"
        >
          🎙️
        </button>

        {/* File Upload */}
        <input
          type="file"
          accept="audio/*"
          onChange={handleTranscribe}
          className="mt-3 p-2 border border-gray-300 rounded-lg shadow-sm bg-white cursor-pointer"
        />

        {/* Language Dropdown */}
        <select
          value={language}
          onChange={(e) => setLanguage(e.target.value)}
          className="mt-2 p-2 border rounded-lg shadow-sm bg-white"
        >
          <option value="python">Python</option>
          <option value="cpp">C++</option>
          <option value="c">C</option>
          <option value="java">Java</option>
          <option value="javascript">JavaScript</option>
        </select>

        {/* Transcript */}
        <textarea
          value={transcript}
          onChange={(e) => setTranscript(e.target.value)}
          placeholder="Transcript will appear here..."
          className="w-full h-24 p-3 border rounded-lg shadow-md mt-4 bg-white"
        />

        {/* Manual Prompt */}
        <textarea
          value={manualPrompt}
          onChange={(e) => setManualPrompt(e.target.value)}
          placeholder="Or type your code request..."
          className="w-full h-24 p-3 border rounded-lg shadow-md mt-2 bg-white"
        />

        {/* Generate Button */}
        <button
          onClick={handleGenerateCode}
          disabled={loading}
          className="mt-3 px-6 py-2 bg-green-500 hover:bg-green-600 text-white font-semibold rounded-lg shadow-md"
        >
          {loading ? "Generating..." : "Generate Code"}
        </button>

        {/* Generated Code */}
        <textarea
          value={generatedCode}
          readOnly
          placeholder="Generated code will appear here..."
          className="w-full h-40 p-3 border rounded-lg shadow-md mt-4 bg-gray-50"
        />

        {/* Compile Button */}
        <button
          onClick={handleCompile}
          disabled={loading}
          className="mt-3 px-6 py-2 bg-blue-500 hover:bg-blue-600 text-white font-semibold rounded-lg shadow-md"
        >
          {loading ? "Compiling..." : "Compile Code"}
        </button>

        {/* Compile Output */}
        <textarea
          value={compileOutput}
          readOnly
          placeholder="Compilation output will appear here..."
          className="w-full h-32 p-3 border rounded-lg shadow-md mt-4 bg-gray-50"
        />
        
      </div>
      
    </div>
    
  );
}

export default App;
