import React, { useState, useRef } from "react";
import axios from "axios";

function App() {
  const [transcript, setTranscript] = useState("");
  const [manualPrompt, setManualPrompt] = useState("");
  const [generatedCode, setGeneratedCode] = useState("");
  const [compileOutput, setCompileOutput] = useState("");
  const [loading, setLoading] = useState(false);
  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);

  // üé§ Handle microphone recording
  const handleMicClick = async () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
      return;
    }

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mimeType = "audio/webm"; // ‚úÖ supported everywhere
      const mediaRecorder = new MediaRecorder(stream, { mimeType });

      mediaRecorderRef.current = mediaRecorder;
      chunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        const audioBlob = new Blob(chunksRef.current, { type: mimeType });
        const formData = new FormData();
        formData.append("file", audioBlob, "recording.webm");

        try {
          const res = await axios.post("http://localhost:5001/api/transcribe", formData, {
            headers: { "Content-Type": "multipart/form-data" },
          });
          setTranscript(res.data.transcript || "");
        } catch (err) {
          console.error("Transcription failed:", err);
          alert("‚ùå Transcription failed. Check backend.");
        }
      };

      mediaRecorder.start();
      setTimeout(() => {
        if (mediaRecorder.state === "recording") {
          mediaRecorder.stop();
        }
      }, 5000);
    } catch (err) {
      console.error("Mic error:", err);
      alert("‚ùå Microphone access failed.");
    }
  };

  // üìù Handle transcription (file upload)
  const handleTranscribe = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post("http://localhost:5001/api/transcribe", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setTranscript(res.data.transcript || "");
    } catch (err) {
      console.error("Transcription failed:", err);
      alert("‚ùå File transcription failed.");
    }
  };

  // ü§ñ Generate code
  const handleGenerateCode = async () => {
    const finalPrompt = manualPrompt || transcript;
    if (!finalPrompt) return alert("Enter a prompt or record audio!");

    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/generate-code", { prompt: finalPrompt });
      setGeneratedCode(res.data.code || "");
    } catch (err) {
      console.error("Code generation failed:", err);
      alert("‚ùå Code generation failed.");
    }
    setLoading(false);
  };

  // ‚ñ∂Ô∏è Compile code
  const handleCompile = async () => {
    if (!generatedCode) return alert("Generate code first!");

    try {
      const res = await axios.post("http://localhost:8000/api/compile", { code: generatedCode });
      setCompileOutput(res.data.output || "");
    } catch (err) {
      console.error("Compilation failed:", err);
      alert("‚ùå Compilation failed.");
    }
  };

  // üíæ Save file
  const handleSaveFile = () => {
    if (!generatedCode) return;
    const blob = new Blob([generatedCode], { type: "text/plain;charset=utf-8" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "generated_code.py";
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center p-6">
      {/* Banner */}
      <div className="w-full bg-gradient-to-r from-purple-600 to-pink-600 p-4 rounded-lg mb-6 text-center">
        <h1 className="text-3xl font-bold">üéôÔ∏è CodeVoice</h1>
        <p className="text-sm">Voice ‚Üí Code ‚Üí Compile ‚Üí Save</p>
      </div>

      {/* Mic Button */}
      <button
        onClick={handleMicClick}
        className="w-20 h-20 bg-red-600 hover:bg-red-700 rounded-full flex items-center justify-center text-2xl shadow-lg mb-4"
      >
        üé§
      </button>

      {/* File Upload */}
      <input type="file" accept="audio/*" onChange={handleTranscribe} className="mb-4" />

      {/* Transcript */}
      <textarea
        className="w-full max-w-xl p-3 rounded-lg bg-gray-800 text-white mb-4"
        rows="3"
        placeholder="Transcript..."
        value={transcript}
        readOnly
      />

      {/* Manual Prompt */}
      <textarea
        className="w-full max-w-xl p-3 rounded-lg bg-gray-800 text-white mb-4"
        rows="3"
        placeholder="Or type your prompt here..."
        value={manualPrompt}
        onChange={(e) => setManualPrompt(e.target.value)}
      />

      {/* Generate Button */}
      <button
        onClick={handleGenerateCode}
        disabled={loading}
        className="w-full max-w-xl bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg shadow mb-4"
      >
        ‚ö° {loading ? "Generating..." : "Generate Code"}
      </button>

      {/* Generated Code */}
      <textarea
        className="w-full max-w-xl p-3 rounded-lg bg-gray-800 text-green-400 mb-4"
        rows="8"
        placeholder="Generated code will appear here..."
        value={generatedCode}
        readOnly
      />

      {/* Compile Button */}
      <button
        onClick={handleCompile}
        disabled={!generatedCode}
        className="w-full max-w-xl bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-lg shadow mb-4"
      >
        ‚ñ∂Ô∏è Compile Code
      </button>

      {/* Save File Button */}
      <button
        onClick={handleSaveFile}
        disabled={!generatedCode}
        className="w-full max-w-xl bg-gray-700 hover:bg-gray-800 text-white py-2 px-4 rounded-lg shadow mb-4"
      >
        üíæ Save Code
      </button>

      {/* Compile Output */}
      <textarea
        className="w-full max-w-xl p-3 rounded-lg bg-gray-800 text-yellow-400"
        rows="4"
        placeholder="Compile output..."
        value={compileOutput}
        readOnly
      />
    </div>
  );
}

export default App;
