import React, { useState } from "react";
import axios from "axios";
import CodeEditor from "./CodeEditor";
import MicInput from "./MicInput";

function App() {
  const [code, setCode] = useState("");
  const [transcript, setTranscript] = useState("");
  const [manualPrompt, setManualPrompt] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");

  const handleTranscript = (text) => {
    setTranscript(text);
    setError("");
  };

  const handleManualPromptChange = (e) => {
    setManualPrompt(e.target.value);
  };

  const generateCode = async () => {
    const promptToSend = transcript.trim() || manualPrompt.trim();
    if (!promptToSend) {
      setError("Please record a prompt or enter text manually.");
      return;
    }

    try {
      setIsLoading(true);
      setError("");
      const response = await axios.post("http://localhost:5000/api/parse", {
        prompt: promptToSend,
      });

      if (response.data && response.data.generated_code) {
        setCode(response.data.generated_code);
      } else {
        setError("No code generated. Please try again.");
        setCode("");
      }
    } catch (err) {
      console.error(err);
      setError("Failed to generate code. Try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <h1 className="text-3xl font-bold text-center mb-4">ðŸŽ¤ CodeVoice</h1>

      <div className="mb-4">
        <MicInput onTranscription={handleTranscript} />
      </div>

      {transcript && (
        <div className="mb-4 p-2 bg-white border rounded shadow">
          <strong>Transcribed Text:</strong>
          <p className="text-gray-800">{transcript}</p>
        </div>
      )}

      <div className="mb-4">
        <label className="block font-medium mb-1">Or enter your prompt:</label>
        <textarea
          value={manualPrompt}
          onChange={handleManualPromptChange}
          rows={3}
          className="w-full p-2 border rounded"
          placeholder="Type your prompt here if mic is not working..."
        />
      </div>

      <div className="flex justify-center">
        <button
          onClick={generateCode}
          className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 transition"
          disabled={isLoading}
        >
          {isLoading ? "Generating..." : "Generate Code"}
        </button>
      </div>

      {error && (
        <div className="text-red-600 font-semibold text-center mt-4">{error}</div>
      )}

      {code && (
        <div className="mt-6">
          <CodeEditor code={code} />
        </div>
      )}
    </div>
  );
}

export default App;
