import React, { useState } from "react";
import MicInput from "./MicInput";
import CodeEditor from "./CodeEditor";

function App() {
  const [transcript, setTranscript] = useState("");

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">CodeVoice Editor</h1>
      <MicInput setTranscript={setTranscript} />
      <CodeEditor transcript={transcript} />
    </div>
  );
}

export default App;
