import React, { useRef, useState } from 'react';
import axios from 'axios';

function MicInput({ onTranscript }) {
  const mediaRecorderRef = useRef(null);
  const [isRecording, setIsRecording] = useState(false);
  const audioChunksRef = useRef([]);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    mediaRecorderRef.current = new MediaRecorder(stream);

    mediaRecorderRef.current.ondataavailable = event => {
      if (event.data.size > 0) {
        audioChunksRef.current.push(event.data);
      }
    };

    mediaRecorderRef.current.onstop = async () => {
      const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
      audioChunksRef.current = [];

      const formData = new FormData();
      formData.append('audio', audioBlob);

      try {
        const response = await axios.post('http://localhost:5001/api/transcribe', formData);
        const transcript = response.data.transcript;
        if (onTranscript) {
          onTranscript(transcript);  // âœ… This sends transcript back to App.js
        }

        // Code Generation Request
        const codeResponse = await axios.post('http://localhost:5000/api/parse', { text: transcript });
        console.log('Generated Code:', codeResponse.data.code);

        // Save to localStorage or handle as needed

      } catch (error) {
        console.error('Transcription or Code Generation Error:', error);
      }
    };

    mediaRecorderRef.current.start();
    setIsRecording(true);
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  return (
    <div className="my-4">
      <button
        onClick={isRecording ? stopRecording : startRecording}
        className={`px-6 py-2 rounded-md text-white ${isRecording ? 'bg-red-500' : 'bg-green-600'}`}
      >
        {isRecording ? 'ðŸ›‘ Stop Recording' : 'ðŸŽ¤ Start Recording'}
      </button>
    </div>
  );
}

export default MicInput;
