import React, { useState, useRef, useCallback, useEffect } from "react";

export default function App() {
  const [transcript, setTranscript] = useState("");
  const [code, setCode] = useState("");
  const [status, setStatus] = useState("");

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // ðŸŽ¤ Start Voice Recording
  const startRecording = useCallback(async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) audioChunksRef.current.push(event.data);
      };

      mediaRecorderRef.current.onstop = handleStopRecording;
      mediaRecorderRef.current.start();
      setStatus("Recording... Speak your command");
    } catch (err) {
      console.error("Microphone error:", err);
      setStatus("Microphone access denied.");
    }
  }, []);

  // ðŸ›‘ Stop Recording
  const stopRecording = useCallback(() => {
    mediaRecorderRef.current?.stop();
    setStatus("Processing audio...");
  }, []);

  // ðŸŽ¯ Handle Recording Stop
  const handleStopRecording = useCallback(async () => {
    const audioBlob = new Blob(audioChunksRef.current, { type: "audio/wav" });
    await sendAudioToBackend(audioBlob);
  }, []);

  // ðŸ“¡ Send Audio to Whisper Backend
  const sendAudioToBackend = useCallback(async (audioBlob) => {
    const formData = new FormData();
    formData.append("file", audioBlob, "recording.wav");

    try {
      const res = await fetch("/api/transcribe", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setTranscript(data.text || "");
      setStatus("Transcription complete");

      if (data.text?.toLowerCase().includes("generate")) {
        generateCode(data.text);
      } else if (data.text?.toLowerCase().includes("compile")) {
        compileCode();
      } else if (data.text?.toLowerCase().includes("save")) {
        saveCode();
      }
    } catch (err) {
      console.error("Transcription error:", err);
      setStatus("Error during transcription.");
    }
  }, []);

  // ðŸ¤– Generate Code from Prompt
  const generateCode = useCallback(async (prompt = transcript) => {
    setStatus("Generating code...");
    try {
      const res = await fetch("/api/generate-code", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt }),
      });
      const data = await res.json();
      setCode(data.code || "");
      setStatus("Code generated successfully");
    } catch (err) {
      console.error("Code generation error:", err);
      setStatus("Error generating code.");
    }
  }, [transcript]);

  // ðŸ–¥ Compile Python Code
  const compileCode = useCallback(async () => {
    setStatus("Compiling code...");
    try {
      const res = await fetch("/api/compile", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code }),
      });
      const data = await res.json();
      alert(`Compilation Output:\n${data.output}`);
      setStatus("Compilation complete");
    } catch (err) {
      console.error("Compilation error:", err);
      setStatus("Error compiling code.");
    }
  }, [code]);

  // ðŸ’¾ Save Code as File
  const saveCode = useCallback(() => {
    const blob = new Blob([code], { type: "text/plain" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "code.py";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    setStatus("Code saved locally");
  }, [code]);

  // ðŸ”„ Auto start recording when page loads
  useEffect(() => {
    startRecording();
  }, [startRecording]);

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold mb-4">ðŸŽ¤ CodeVoice</h1>
      <p className="mb-6 text-gray-300">{status || "Ready for your command"}</p>

      {/* Transcript */}
      <textarea
        value={transcript}
        onChange={(e) => setTranscript(e.target.value)}
        placeholder="Your transcript or manual prompt..."
        className="w-full max-w-2xl p-4 mb-4 bg-gray-800 rounded-lg text-white border border-gray-700"
      />

      {/* Code Box */}
      <textarea
        value={code}
        onChange={(e) => setCode(e.target.value)}
        placeholder="Generated code will appear here..."
        className="w-full max-w-2xl p-4 mb-4 bg-gray-800 rounded-lg text-green-400 font-mono border border-gray-700"
      />

      {/* Actions */}
      <div className="flex gap-4">
        <button
          onClick={() => generateCode()}
          className="px-6 py-3 bg-blue-600 hover:bg-blue-700 rounded-lg"
        >
          Generate Code
        </button>
        <button
          onClick={compileCode}
          className="px-6 py-3 bg-yellow-600 hover:bg-yellow-700 rounded-lg"
        >
          Compile
        </button>
        <button
          onClick={saveCode}
          className="px-6 py-3 bg-purple-600 hover:bg-purple-700 rounded-lg"
        >
          Save
        </button>
      </div>
    </div>
  );
}
