import React, { useState } from 'react';
import CodeEditor from './components/Editor';
import MicInput from './components/MicInput';
import axios from 'axios';

function App() {
  const [code, setCode] = useState('');

  const handleVoiceCommand = async (spokenText) => {
    console.log("Voice Input:", spokenText);
    try {
      const response = await axios.post('http://localhost:5000/api/parse', { text: spokenText });
      if (response.data?.code) {
        setCode((prev) => prev + '\n' + response.data.code);
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Backend error: " + error.message);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white font-sans flex flex-col items-center px-4 py-6">
      <h1 className="text-3xl font-bold mb-4">CodeVoice – Voice-Controlled IDE</h1>

      <MicInput onTranscript={handleVoiceCommand} />

      <div className="w-full max-w-4xl mt-6 border border-gray-700 rounded-lg shadow-lg overflow-hidden">
        <CodeEditor code={code} setCode={setCode} />
      </div>

      <footer className="mt-6 text-sm text-gray-500">
        © 2025 CodeVoice • Powered by React, Monaco & Tailwind
      </footer>
    </div>
  );
}

export default App;
