import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import CodeEditor from "./CodeEditor";
import VoiceControl from "./VoiceControl";

function App() {
  const [transcript, setTranscript] = useState("");
  const [manualPrompt, setManualPrompt] = useState("");
  const [generatedCode, setGeneratedCode] = useState("");
  const [compileOutput, setCompileOutput] = useState("");
  const [loading, setLoading] = useState(false);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // üé§ Handle mic recording
  const handleMicClick = async () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
      return;
    }

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });

      // ‚úÖ Use audio/webm (browser supported)
      const mediaRecorder = new MediaRecorder(stream, { mimeType: "audio/webm" });
      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: "audio/webm" });
        await handleTranscribe(audioBlob);
      };

      mediaRecorder.start();
    } catch (err) {
      console.error("Error accessing microphone:", err);
      alert("Microphone access failed. Please check permissions.");
    }
  };

  // üìù Send audio to backend for transcription
  const handleTranscribe = async (audioBlob) => {
    const formData = new FormData();
    formData.append("file", audioBlob, "recording.webm");

    try {
      const response = await axios.post("http://localhost:8000/transcribe/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (response.data.transcript) {
        setTranscript(response.data.transcript);
      } else {
        alert("No speech detected");
      }
    } catch (error) {
      console.error("Error transcribing audio:", error);
      alert("Transcription failed.");
    }
  };

  // ‚ö° Generate code
  const handleGenerateCode = async () => {
    const prompt = transcript || manualPrompt;
    if (!prompt) return;

    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/api/generate-code", {
        prompt,
      });
      setGeneratedCode(response.data.code || "No code generated.");
    } catch (error) {
      console.error("Error generating code:", error);
      alert("Code generation failed.");
    } finally {
      setLoading(false);
    }
  };

  // ‚ñ∂Ô∏è Compile Python code
  const handleCompileCode = async () => {
    if (!generatedCode) return;

    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/api/compile", {
        code: generatedCode,
        language: "python",
      });
      setCompileOutput(response.data.output || "No output.");
    } catch (error) {
      console.error("Error compiling code:", error);
      alert("Compilation failed.");
    } finally {
      setLoading(false);
    }
  };

  // Save code to file
  const handleSaveFile = () => {
    if (!generatedCode) return;

    const blob = new Blob([generatedCode], { type: "text/plain" });
    const link = document.createElement("a");
    link.href = window.URL.createObjectURL(blob);
    link.download = "generated_code.py";
    link.click();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-6 bg-gray-50">
      <h1 className="text-3xl font-bold mb-6">CodeVoice</h1>

      {/* üé§ Record button */}
      <button
        onClick={handleMicClick}
        className="px-6 py-2 mb-4 bg-blue-500 text-white rounded-lg shadow hover:bg-blue-600"
      >
        üé§ Record / Stop
      </button>

      {/* üìù Transcript */}
      <textarea
        className="w-full max-w-lg p-3 mb-4 border rounded-lg shadow"
        placeholder="Transcript will appear here..."
        value={transcript}
        readOnly
        rows={3}
      />

      {/* ‚úçÔ∏è Manual prompt */}
      <textarea
        className="w-full max-w-lg p-3 mb-4 border rounded-lg shadow"
        placeholder="Or type your prompt here..."
        value={manualPrompt}
        onChange={(e) => setManualPrompt(e.target.value)}
        rows={3}
      />

      {/* ‚ö° Generate button */}
      <button
        onClick={handleGenerateCode}
        disabled={loading}
        className="px-6 py-2 mb-6 bg-green-500 text-white rounded-lg shadow hover:bg-green-600 disabled:opacity-50"
      >
        ‚ö° Generate Code
      </button>

      {/* üñ•Ô∏è Code editor */}
      <CodeEditor code={generatedCode} setCode={setGeneratedCode} />

      {/* ‚ñ∂Ô∏è Compile + üíæ Save buttons */}
      <div className="flex space-x-4 mt-4">
        <button
          onClick={handleCompileCode}
          disabled={loading}
          className="px-6 py-2 bg-purple-500 text-white rounded-lg shadow hover:bg-purple-600 disabled:opacity-50"
        >
          ‚ñ∂Ô∏è Compile
        </button>
        <button
          onClick={handleSaveFile}
          className="px-6 py-2 bg-gray-500 text-white rounded-lg shadow hover:bg-gray-600"
        >
          üíæ Save File
        </button>
      </div>

      {/* üìú Compile output */}
      {compileOutput && (
        <pre className="w-full max-w-lg p-3 mt-6 bg-black text-green-400 rounded-lg shadow overflow-x-auto">
          {compileOutput}
        </pre>
      )}

      {/* üéôÔ∏è Voice control */}
      <VoiceControl
        onGenerate={handleGenerateCode}
        onCompile={handleCompileCode}
        onSave={handleSaveFile}
      />
    </div>
  );
}

export default App;
