import React, { useEffect, useRef, useState } from "react";

const VoiceControl = ({ setTranscript }) => {
  const recognitionRef = useRef(null);
  const [isActivated, setIsActivated] = useState(false);

  useEffect(() => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Speech Recognition is not supported in this browser.");
      return;
    }

    // Passive recognizer to detect commands
    const passiveRecognition = new SpeechRecognition();
    passiveRecognition.continuous = true;
    passiveRecognition.lang = "en-US";
    passiveRecognition.interimResults = false;

    // Active recognizer to listen for prompts
    const activeRecognition = new SpeechRecognition();
    activeRecognition.continuous = true;
    activeRecognition.lang = "en-US";
    activeRecognition.interimResults = false;

    recognitionRef.current = activeRecognition;

    // âœ… Handle commands in passive mode
    passiveRecognition.onresult = (event) => {
      const command = event.results[event.results.length - 1][0].transcript.trim().toLowerCase();
      console.log("Heard (passive):", command);

      if (command.includes("start recording") && !isActivated) {
        setIsActivated(true);
        passiveRecognition.stop();
        activeRecognition.start();
        console.log("ðŸŽ¤ Voice command: Start recording");
      } else if (command.includes("scroll up")) {
        window.scrollBy({ top: -200, behavior: "smooth" });
        console.log("â¬†ï¸ Voice command: Scroll up");
      } else if (command.includes("scroll down")) {
        window.scrollBy({ top: 200, behavior: "smooth" });
        console.log("â¬‡ï¸ Voice command: Scroll down");
      } else if (command.includes("stop recording")) {
        if (isActivated) {
          setIsActivated(false);
          activeRecognition.stop();
          console.log("ðŸ›‘ Voice command: Stop recording");
        }
      }
    };

    passiveRecognition.onerror = (e) => {
      console.error("Passive recognition error:", e);
    };

    passiveRecognition.start();

    // âœ… Handle code input after recording starts
    activeRecognition.onresult = (event) => {
      const result = event.results[event.results.length - 1][0].transcript.trim();
      console.log("ðŸŽ™ï¸ Captured input:", result);
      setTranscript(result);
      activeRecognition.stop();
      setIsActivated(false);
      // Re-enable passive recognition to wait for next command
      passiveRecognition.start();
    };

    activeRecognition.onerror = (event) => {
      console.error("Active recognition error:", event.error);
    };

    return () => {
      passiveRecognition.stop();
      activeRecognition.stop();
    };
  }, [isActivated, setTranscript]);

  return (
    <div className="text-center mb-4">
      <p className="text-lg text-gray-700">
        ðŸŽ§ Say <strong>"start recording"</strong>, <strong>"scroll up"</strong>, <strong>"scroll down"</strong>, or <strong>"stop recording"</strong>.
      </p>
    </div>
  );
};

export default VoiceControl;
