import React, { useEffect, useState } from 'react';

function CodeEditor() {
  const [code, setCode] = useState('');

  useEffect(() => {
    const storedCode = localStorage.getItem('generatedCode');
    if (storedCode) setCode(storedCode);
  }, []);

  return (
    <div className="p-6 bg-white rounded-lg shadow w-full max-w-4xl mx-auto">
      <h2 className="text-xl font-semibold mb-2">ðŸ§  Generated Code</h2>
      <textarea
        value={code || "// Your generated code will appear here..."}
        onChange={(e) => setCode(e.target.value)}
        rows={18}
        className="w-full p-3 border border-gray-300 rounded font-mono text-sm bg-gray-50 resize-none"
      />
    </div>
  );
}

export default CodeEditor;
