// App.js
import React, { useState } from "react";
import VoiceControl from "./VoiceControl";
import axios from "axios";

function App() {
  const [code, setCode] = useState("");
  const [output, setOutput] = useState("");
  const [transcript, setTranscript] = useState("");

  // Generate Code
  const handleGenerate = async () => {
    try {
      const res = await axios.post("http://localhost:5001/api/generate-code", {
        prompt: transcript || "Write a simple Python program."
      });
      setCode(res.data.code);
    } catch (err) {
      console.error("Error generating code:", err);
    }
  };

  // Compile Code
  const handleCompile = async () => {
    try {
      const res = await axios.post("http://localhost:5001/api/compile", { code });
      setOutput(res.data.output);
    } catch (err) {
      console.error("Error compiling code:", err);
    }
  };

  // Save Code
  const handleSave = () => {
    const blob = new Blob([code], { type: "text/plain;charset=utf-8" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "code.py";
    link.click();
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold text-center">CodeVoice</h1>

      {/* Voice Commands */}
      <VoiceControl
        onGenerate={handleGenerate}
        onCompile={handleCompile}
        onSave={handleSave}
        setTranscript={setTranscript}
      />

      {/* Manual Buttons */}
      <div className="flex gap-2 justify-center mt-4">
        <button className="bg-blue-500 text-white px-4 py-2 rounded" onClick={handleGenerate}>
          Generate
        </button>
        <button className="bg-green-500 text-white px-4 py-2 rounded" onClick={handleCompile}>
          Compile
        </button>
        <button className="bg-yellow-500 text-white px-4 py-2 rounded" onClick={handleSave}>
          Save
        </button>
      </div>

      {/* Output Sections */}
      <textarea
        className="w-full mt-4 p-2 border rounded"
        value={code}
        onChange={(e) => setCode(e.target.value)}
        rows={10}
      />
      <pre className="bg-gray-100 p-2 mt-4 rounded">{output}</pre>
    </div>
  );
}

export default App;
